const actionType = workflow?.activity?.actionType;
if (!["mwak", "rwak"].includes(actionType)) return {};
const damageItem = workflow?.damageItem;
const details = damageItem?.damageDetail;
if (!details?.length) return {};

let updated = false;
for (const entry of details) {
  const original = entry.value ?? 0;
  const reduced = Math.floor(original * 0.5);
  if (reduced !== original) {
    entry.value = reduced;
    if (typeof entry.damage === "number") entry.damage = reduced;
    updated = true;
  }
}
if (!updated) return {};

let damageTotal = 0;
for (const entry of details) {
  const value = entry.value ?? 0;
  if (typeof entry.damage === "number") entry.damage = value;
  if (!["temphp", "midi-none", "healing"].includes(entry.type)) damageTotal += value;
}
if (typeof damageItem.totalDamage === "number") damageItem.totalDamage = damageTotal;
if (typeof damageItem.appliedDamage === "number") damageItem.appliedDamage = damageTotal;
return {};
